import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [stats, setStats] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchStats();
    fetchDocuments();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await fetch('http://localhost:8000/stats/');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setStats(data);
    } catch (error) {
      console.error('Error fetching stats:', error);
      setError('Failed to load statistics. Make sure backend is running.');
    }
  };

  const fetchDocuments = async () => {
    try {
      const response = await fetch('http://localhost:8000/documents/');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setDocuments(data);
    } catch (error) {
      console.error('Error fetching documents:', error);
      setError('Failed to load documents.');
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    setError(null);
    setSuccessMessage('');
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:8000/upload/', {
        method: 'POST',
        body: formData,
      });
      
      if (response.ok) {
        const result = await response.json();
        const message = result.is_duplicate 
          ? `Duplicate detected! ${file.name} matches an existing document.`
          : `Document ${file.name} uploaded successfully!`;
        
        setSuccessMessage(message);
        fetchStats();
        fetchDocuments();
      } else {
        const errorText = await response.text();
        setError(`Upload failed: ${errorText}`);
      }
    } catch (error) {
      setError(`Error uploading file: ${error.message}`);
    } finally {
      setUploading(false);
      event.target.value = ''; // Reset file input
    }
  };

  const formatBytes = (bytes) => {
    if (bytes === 0 || bytes === undefined || bytes === null) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const calculateSavingsDisplay = (original, optimized) => {
    if (original === 0 || original === undefined || optimized === undefined) {
      return { bytes: 0, percent: 0, isPositive: true };
    }
    
    const bytesSaved = original - optimized;
    const percent = ((bytesSaved / original) * 100);
    
    return {
      bytes: Math.abs(bytesSaved),
      percent: Math.abs(percent),
      isPositive: bytesSaved >= 0
    };
  };

  const getSavingsClassName = (original, optimized) => {
    const savings = calculateSavingsDisplay(original, optimized);
    return savings.isPositive ? 'positive-savings' : 'negative-savings';
  };

  const getSavingsText = (original, optimized) => {
    const savings = calculateSavingsDisplay(original, optimized);
    const prefix = savings.isPositive ? '' : '+';
    return `${prefix}${formatBytes(savings.bytes)} (${prefix}${savings.percent.toFixed(1)}%)`;
  };

  const getDocumentStatus = (doc) => {
    if (doc.is_duplicate) {
      return doc.original_document_id 
        ? `Duplicate of document #${doc.original_document_id}`
        : 'Duplicate';
    }
    return 'Original';
  };

  const createDemoFiles = async () => {
    // Create demo file uploads
    const demoFiles = [
      new File(['This is a test PDF document content.'], 'test1.pdf', { type: 'application/pdf' }),
      new File(['This is a test PDF document content.'], 'test2.pdf', { type: 'application/pdf' }), // Duplicate
      new File(['Different content here for a text file.'], 'notes.txt', { type: 'text/plain' }),
    ];

    for (const file of demoFiles) {
      const formData = new FormData();
      formData.append('file', file);
      
      try {
        await fetch('http://localhost:8000/upload/', {
          method: 'POST',
          body: formData,
        });
      } catch (error) {
        console.error('Error uploading demo file:', error);
      }
      await new Promise(resolve => setTimeout(resolve, 500)); // Delay between uploads
    }
    
    setSuccessMessage('Demo files uploaded! Check for duplicate detection.');
    fetchStats();
    fetchDocuments();
  };

  const clearAllData = async () => {
    if (window.confirm('Are you sure you want to clear all data?')) {
      // For demo, reload page and backend will reset
      window.location.reload();
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>üìÅ DocSlim - AI Document Management</h1>
        <p>Intelligent document reduction and deduplication</p>
      </header>

      {error && (
        <div className="error-message">
          ‚ö†Ô∏è {error}
        </div>
      )}

      {successMessage && (
        <div className="success-message">
          ‚úÖ {successMessage}
        </div>
      )}

      <div className="upload-section">
        <h2>üì§ Upload Document</h2>
        <div className="upload-area">
          <input 
            type="file" 
            id="fileInput"
            onChange={handleFileUpload}
            disabled={uploading}
          />
          <label htmlFor="fileInput" className="upload-button">
            {uploading ? 'Uploading...' : 'Choose File'}
          </label>
          <p className="upload-hint">Drag & drop files or click to browse</p>
          <p className="file-types">Supports: PDF, DOCX, TXT, Images</p>
        </div>
        
        <div className="demo-buttons">
          <button onClick={createDemoFiles} className="demo-button">
            Upload Demo Files
          </button>
          <button onClick={clearAllData} className="clear-button">
            Clear All Data
          </button>
        </div>
      </div>

      {stats && (
        <div className="stats-section">
          <h2>üìä Storage Statistics</h2>
          <div className="stats-grid">
            <div className="stat-card">
              <h3>Total Documents</h3>
              <p className="stat-value">{stats.total_documents}</p>
              <p className="stat-label">All files processed</p>
            </div>
            <div className="stat-card">
              <h3>Original Size</h3>
              <p className="stat-value">{formatBytes(stats.total_original_size)}</p>
              <p className="stat-label">Before optimization</p>
            </div>
            <div className="stat-card">
              <h3>Optimized Size</h3>
              <p className="stat-value">{formatBytes(stats.total_optimized_size)}</p>
              <p className="stat-label">After optimization</p>
            </div>
            <div className="stat-card">
              <h3>Total Savings</h3>
              <p className={`stat-value ${getSavingsClassName(stats.total_original_size, stats.total_optimized_size)}`}>
                {getSavingsText(stats.total_original_size, stats.total_optimized_size)}
              </p>
              <p className="stat-label">Storage efficiency</p>
            </div>
          </div>
        </div>
      )}

      <div className="documents-section">
        <h2>üìã Recent Documents</h2>
        {documents.length === 0 ? (
          <div className="empty-state">
            <p>No documents uploaded yet. Upload files to see data reduction in action!</p>
          </div>
        ) : (
          <table className="documents-table">
            <thead>
              <tr>
                <th>Filename</th>
                <th>Type</th>
                <th>Original Size</th>
                <th>Optimized Size</th>
                <th>Savings</th>
                <th>Tier</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {documents.slice(0, 10).map(doc => {
                const savingsClass = getSavingsClassName(doc.original_size, doc.optimized_size);
                const savingsText = getSavingsText(doc.original_size, doc.optimized_size);
                const status = getDocumentStatus(doc);
                
                return (
                  <tr key={doc.id} className={doc.is_duplicate ? 'duplicate-row' : ''}>
                    <td>
                      <div className="filename-cell">
                        {doc.original_filename}
                        {doc.is_duplicate && <span className="duplicate-icon">üîÑ</span>}
                      </div>
                    </td>
                    <td><span className="file-type-badge">{doc.file_type || 'unknown'}</span></td>
                    <td>{formatBytes(doc.original_size)}</td>
                    <td>{formatBytes(doc.optimized_size)}</td>
                    <td className={`savings-cell ${savingsClass}`}>
                      {savingsText}
                    </td>
                    <td>
                      <span className={`tier-badge tier-${doc.tier || 'cold'}`}>
                        {doc.tier || 'cold'}
                      </span>
                    </td>
                    <td>
                      <span className={doc.is_duplicate ? 'duplicate-status' : 'original-status'}>
                        {status}
                      </span>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </div>

      <div className="explanation-section">
        <h2>üîç How Data Reduction Works</h2>
        <div className="explanation-cards">
          <div className="explanation-card">
            <h3>üìù Deduplication</h3>
            <p>Identical files are stored only once. Duplicates reference the original, saving storage space.</p>
          </div>
          <div className="explanation-card">
            <h3>üìâ Compression</h3>
            <p>Files are optimized using algorithms that reduce size while preserving content.</p>
          </div>
          <div className="explanation-card">
            <h3>üè∑Ô∏è Intelligent Tiering</h3>
            <p>Files are categorized (Hot/Warm/Cold) based on usage, allowing different optimization strategies.</p>
          </div>
          <div className="explanation-card">
            <h3>üìä Analytics</h3>
            <p>Track storage savings, identify patterns, and optimize your document management strategy.</p>
          </div>
        </div>
      </div>

      <footer className="footer">
        <p>DocSlim - AI Document Management System | Demonstrating Data Reduction Techniques</p>
        <p>Backend: http://localhost:8000 | Total Documents: {stats?.total_documents || 0}</p>
      </footer>
    </div>
  );
}

export default App;
