import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [stats, setStats] = useState(null);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchStats();
    fetchDocuments();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await fetch('http://localhost:8000/stats/');
      const data = await response.json();
      setStats(data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const fetchDocuments = async () => {
    try {
      const response = await fetch('http://localhost:8000/documents/');
      const data = await response.json();
      setDocuments(data);
    } catch (error) {
      console.error('Error fetching documents:', error);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:8000/upload/', {
        method: 'POST',
        body: formData,
      });
      
      if (response.ok) {
        fetchStats();
        fetchDocuments();
      }
    } catch (error) {
      console.error('Upload error:', error);
    } finally {
      setUploading(false);
      event.target.value = '';
    }
  };

  const formatBytes = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getSavingsText = (original, optimized) => {
    if (original === 0 || original === undefined) return '0 Bytes (0%)';
    
    const savings = original - optimized;
    const percent = ((savings / original) * 100).toFixed(1);
    
    if (savings >= 0) {
      return `${formatBytes(savings)} (${percent}%)`;
    } else {
      return `+${formatBytes(Math.abs(savings))} (+${Math.abs(percent)}%)`;
    }
  };

  return (
    <div className="App">
      <header>
        <h1>DocSlim - AI Document Management</h1>
        <p>Intelligent document reduction and deduplication</p>
      </header>

      <div className="upload-section">
        <h2>Upload Document</h2>
        <div className="upload-box">
          <input 
            type="file" 
            id="fileInput"
            onChange={handleFileUpload}
            disabled={uploading}
          />
          <label htmlFor="fileInput" className="upload-btn">
            {uploading ? 'Processing...' : 'Choose File'}
          </label>
          <p>Drag & drop or click to browse</p>
        </div>
      </div>

      {stats && (
        <div className="stats-section">
          <h2>Storage Statistics</h2>
          <div className="stats-grid">
            <div className="stat-card">
              <h3>Total Documents</h3>
              <p className="stat-value">{stats.total_documents}</p>
            </div>
            <div className="stat-card">
              <h3>Original Size</h3>
              <p className="stat-value">{formatBytes(stats.total_original_size)}</p>
            </div>
            <div className="stat-card">
              <h3>Optimized Size</h3>
              <p className="stat-value">{formatBytes(stats.total_optimized_size)}</p>
            </div>
            <div className="stat-card">
              <h3>Total Savings</h3>
              <p className="stat-value savings">
                {getSavingsText(stats.total_original_size, stats.total_optimized_size)}
              </p>
            </div>
          </div>
        </div>
      )}

      <div className="documents-section">
        <h2>Recent Documents</h2>
        <table className="documents-table">
          <thead>
            <tr>
              <th>Filename</th>
              <th>Type</th>
              <th>Original</th>
              <th>Optimized</th>
              <th>Savings</th>
              <th>Tier</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {documents.slice(0, 10).map(doc => {
              const savingsText = getSavingsText(doc.original_size, doc.optimized_size);
              const isPositive = (doc.original_size - doc.optimized_size) >= 0;
              
              return (
                <tr key={doc.id} className={doc.is_duplicate ? 'duplicate' : ''}>
                  <td>{doc.original_filename}</td>
                  <td>{doc.file_type}</td>
                  <td>{formatBytes(doc.original_size)}</td>
                  <td>{formatBytes(doc.optimized_size)}</td>
                  <td className={isPositive ? 'positive' : 'negative'}>
                    {savingsText}
                  </td>
                  <td>
                    <span className={`tier-badge tier-${doc.tier}`}>
                      {doc.tier}
                    </span>
                  </td>
                  <td>
                    {doc.is_duplicate ? (
                      <span className="duplicate-badge">Duplicate</span>
                    ) : (
                      <span className="original-badge">Original</span>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
